package co.uk.offerland.job_track.application.usecases.user;import java.time.Instant;import org.springframework.stereotype.Service;import co.uk.offerland.job_track.application.dto.user.UserCreateResponse;import co.uk.offerland.job_track.infrastructure.persistence.jdbc.UserJdbcRepository;import co.uk.offerland.job_track.domain.entity.UserEntity;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;@Slf4j@Service@RequiredArgsConstructorpublic class CreateUserUseCase {    private final UserJdbcRepository userRepository;    public UserCreateResponse createNewUser(String source) {        UserEntity newUser = new UserEntity();        newUser.setSource(source);        newUser.setCreatedAt(Instant.now());        newUser.setUpdatedAt(Instant.now());        UserEntity savedUser = userRepository.save(newUser);        log.info("New user saved successfully userId:[{}]", savedUser.getId());        return new UserCreateResponse(savedUser.getId().toString());    }}