package co.uk.offerland.job_track.application.usecases.user;import co.uk.offerland.job_track.application.dto.user.UserCreateResponse;import co.uk.offerland.job_track.domain.entity.nosql.UserEntity;import co.uk.offerland.job_track.infrastructure.persistence.UserRepository;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import reactor.core.publisher.Mono;@Slf4j@Service@RequiredArgsConstructorpublic class CreateUserUseCase {    private final UserRepository userRepository;    public Mono<UserCreateResponse> createNewUser(String source) {        return userRepository.save(new UserEntity(source))                .map(entity -> new UserCreateResponse(entity.getUserId().toString()))                .doOnNext(response -> log.info("New user saved successfully userId:[{}]", response.getUserId()))                .doOnError(error -> log.error("Error saving new user: {}", error.getMessage()));    }}