package co.uk.offerland.job_track.config;import co.uk.offerland.job_track.infrastructure.ai.AIAssistant;import dev.langchain4j.memory.chat.MessageWindowChatMemory;import dev.langchain4j.model.openai.OpenAiChatModel;import dev.langchain4j.service.AiServices;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class AIConfig {    public static final double CONFIDENCE_THRESHOLD = 0.97;    @Value("${open.api.key}")    private String openApiKey;    @Bean    public OpenAiChatModel chatLanguageModel() {        return OpenAiChatModel.builder()                .modelName("gpt-4o-mini")                .apiKey(openApiKey)                .responseFormat("json_object")                .strictJsonSchema(false)                .build();    }    @Bean    public AIAssistant aiAssistant() {        return AiServices.builder(AIAssistant.class)                .chatLanguageModel(chatLanguageModel())                .chatMemoryProvider(memoryId -> MessageWindowChatMemory.withMaxMessages(10))                .build();    }}