package co.uk.offerland.job_track.config;import java.time.Duration;import java.time.temporal.ChronoUnit;import co.uk.offerland.job_track.application.dto.CompanyVisaSponsorshipDto;import co.uk.offerland.job_track.application.dto.RecognizeJobUrlResponse;import org.springframework.cache.annotation.EnableCaching;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import com.github.benmanes.caffeine.cache.Caffeine;@Configuration@EnableCachingpublic class CacheConfig {    public static final int MAXIMUM_CACHE_OBJECT_SIZE = 50000;    public static final int MAX_AMOUNT_VISA_SPONSORSHIP = 30;    public static final int MAX_AMOUNT_JOB_URL = 15;    @Bean    public com.github.benmanes.caffeine.cache.Cache<String, CompanyVisaSponsorshipDto> visaSponsorshipCache(){        return Caffeine.newBuilder()                .expireAfterWrite(Duration.of(MAX_AMOUNT_VISA_SPONSORSHIP, ChronoUnit.DAYS))                .maximumSize(MAXIMUM_CACHE_OBJECT_SIZE)                .build();    }    @Bean    public com.github.benmanes.caffeine.cache.Cache<String, RecognizeJobUrlResponse> jobUrlcache(){        return Caffeine.newBuilder()                .expireAfterWrite(Duration.of(MAX_AMOUNT_JOB_URL, ChronoUnit.DAYS))                .maximumSize(MAXIMUM_CACHE_OBJECT_SIZE)                .build();    }}