package co.uk.offerland.job_track.config;import java.time.Duration;import java.time.temporal.ChronoUnit;import org.springframework.cache.annotation.EnableCaching;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import com.github.benmanes.caffeine.cache.Caffeine;import co.uk.offerland.job_track.application.dto.CompanyVisaSponsorship;@Configuration@EnableCachingpublic class CacheConfig {    public static final int MAXIMUM_CACHE_OBJECT_SIZE = 50000;    public static final int MAX_AMOUNT_STORE_DATA_IN_DAYS = 30;    @Bean    public com.github.benmanes.caffeine.cache.Cache<String, CompanyVisaSponsorship> cache(){        return Caffeine.newBuilder()            .expireAfterWrite(Duration.of(MAX_AMOUNT_STORE_DATA_IN_DAYS, ChronoUnit.DAYS))            .maximumSize(MAXIMUM_CACHE_OBJECT_SIZE)            .build();    }}