package co.uk.offerland.job_track.domain.entity;import java.util.UUID;import org.hibernate.annotations.UuidGenerator;import org.springframework.data.jpa.domain.support.AuditingEntityListener;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.EntityListeners;import jakarta.persistence.GeneratedValue;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.NoArgsConstructor;import lombok.ToString;@Data@EqualsAndHashCode@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "company_visa_sponsorship")@org.springframework.data.relational.core.mapping.Table(name = "company_visa_sponsorship")@ToString@EntityListeners(AuditingEntityListener.class)public class CompanyVisaSponsorshipEntity {    public static final CompanyVisaSponsorshipEntity DEFAULT_VALUE = new CompanyVisaSponsorshipEntity(null, "unknown", "unknown", "unknown");    @Id    @GeneratedValue    @UuidGenerator(style = UuidGenerator.Style.TIME)    @Column(name = "id", nullable = false, updatable = false)    private UUID id;    @Column(name = "company", nullable = false)    private String company;    @Column(name = "location", nullable = false)    private String location;    @Column(name = "visa_sponsorship", nullable = false)    private String visaSponsorship;}