package co.uk.offerland.job_track.domain.entity;import lombok.Getter;import lombok.Setter;import lombok.ToString;import org.springframework.data.annotation.Id;import org.springframework.data.relational.core.mapping.Column;import org.springframework.data.relational.core.mapping.Table;import java.util.UUID;@Getter@Setter@ToString@Table(name = "contact_person")public class ContactPersonEntity {    @Id    private UUID id;    @Column("first_name")    private String firstName;    @Column("first_name")    private String lastName;    @Column("phone")    private String phone;    @Column("email")    private String email;    @Column("linkedin")    private String linkedin;    private JobEntity job;}