package co.uk.offerland.job_track.domain.entity;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import lombok.ToString;import java.util.UUID;@Entity@Getter@Setter@ToString@Table(name = "contact_person")public class ContactPersonEntity {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private UUID id;    @JoinColumn(name = "first_name")    private String firstName;    @JoinColumn(name = "first_name")    private String lastName;    @JoinColumn(name = "phone")    private String phone;    @JoinColumn(name = "email")    private String email;    @JoinColumn(name = "linkedin")    private String linkedin;    @OneToOne    @JoinColumn(name = "job_id")    private JobEntity job;}