package co.uk.offerland.job_track.domain.entity;public enum JobPhase {    SAVED("Saved"),    APPLIED("Applied"),    SCREENING_CALL("Screening Call"),    ASSESSMENT_TEST("Assessment Test"),    FIRST_ROUND("1st Round"),    SECOND_ROUND("2nd Round"),    THIRD_ROUND("3rd Round"),    NEGOTIATION("Negotiation"),    FINAL_ROUND("Final Round"),    OFFER_STATUS("Offer Status");    private final String label;    JobPhase(String label) {        this.label = label;    }    public String getLabel() {        return label;    }    public static JobPhase fromCode(String label) {        for (JobPhase phase : values()) {            if (phase.label.equals(label)) {                return phase;            }        }        throw new IllegalArgumentException("Unknown label: " + label);    }}