package co.uk.offerland.job_track.domain.entity;import java.time.Instant;import java.util.UUID;import org.hibernate.annotations.UuidGenerator;import jakarta.persistence.*;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.NoArgsConstructor;import lombok.ToString;@Data@EqualsAndHashCode@NoArgsConstructor@Entity@Table(name = "job_phases")@org.springframework.data.relational.core.mapping.Table(name = "job_phases")@ToStringpublic class JobPhaseEntity {    @Id    @GeneratedValue    @UuidGenerator(style = UuidGenerator.Style.TIME)    @Column(name = "id", nullable = false, updatable = false)    private UUID id;    @ManyToOne    @JoinColumn(name = "job_id", nullable = false)    private JobEntity job;    @Column(name = "phase_name", nullable = false)    private String phaseName;    @Column(name = "date")    private Instant lastUpdatedDate;    @Column(name = "interview_schedule_time")    private Instant interviewScheduleTime;    @Enumerated(EnumType.STRING)    @Column(name = "status", nullable = false)    private JobPhaseStatus status;    @Column(name = "order_index", nullable = false)    private int orderIndex;    @Column(name = "active_status")    private String activeStatus;}