package co.uk.offerland.job_track.domain.entity;import java.util.Optional;import java.util.stream.Stream;public enum JobPhaseStatus {    NOT_STARTED,    IN_PROGRESS,    COMPLETED,    DECLINED,    REJECTED;    public static Optional<JobPhaseStatus> fromString(String value) {        return Stream.of(JobPhaseStatus.values())            .filter(status -> status.name().equalsIgnoreCase(value))            .findFirst();    }}