package co.uk.offerland.job_track.domain.entity;import java.time.Instant;import java.util.ArrayList;import java.util.List;import java.util.UUID;import org.hibernate.annotations.UuidGenerator;import org.springframework.data.annotation.CreatedDate;import org.springframework.data.annotation.LastModifiedDate;import org.springframework.data.jpa.domain.support.AuditingEntityListener;import jakarta.persistence.CascadeType;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.EntityListeners;import jakarta.persistence.FetchType;import jakarta.persistence.GeneratedValue;import jakarta.persistence.Id;import jakarta.persistence.OneToMany;import jakarta.persistence.Table;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.NoArgsConstructor;import lombok.ToString;@Data@EqualsAndHashCode@NoArgsConstructor@Entity@Table(name = "users")@org.springframework.data.relational.core.mapping.Table(name = "users")@ToString(exclude = {"jobs"})@EntityListeners(AuditingEntityListener.class)public class UserEntity {    @Id    @GeneratedValue    @UuidGenerator(style = UuidGenerator.Style.TIME)    @Column(name = "id", nullable = false, updatable = false)    private UUID id;    @Column(name = "source", nullable = false)    private String source;    @OneToMany(mappedBy = "user", cascade = CascadeType.ALL, orphanRemoval = true)    private List<JobEntity> jobs = new ArrayList<>();    @Column(name = "created_at", nullable = false, updatable = false)    @CreatedDate    private Instant createdAt;    @Column(name = "updated_at")    @LastModifiedDate    private Instant updatedAt;}