package co.uk.offerland.job_track.domain.entity.nosql;import lombok.Data;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;import java.util.ArrayList;import java.util.List;import java.time.Instant;import java.util.UUID;@Data@Document(collection = "users")public class User {    @Id    private String id;    @Field("userId")    @Indexed(unique = true)    private UUID userId = UUID.randomUUID();    private String source;    private Instant createdAt;    private Instant updatedAt;    @Field("jobs")    private List<Job> jobs = new ArrayList<>();    private InterviewStat interviewStat;    public User(String source) {        this.source = source;        this.createdAt = Instant.now();        this.updatedAt = Instant.now();        interviewStat = new InterviewStat();    }}