package co.uk.offerland.job_track.infrastructure.controller;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import co.uk.offerland.job_track.application.dto.CompanyJobTitleDetailsRequest;import co.uk.offerland.job_track.application.dto.CompanyVisaSponsorship;import co.uk.offerland.job_track.application.usecases.job.SearchVisaSponsorshipUseCase;import jakarta.validation.Valid;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import reactor.core.publisher.Mono;@Slf4j@RestController@RequestMapping("/api/companies")@RequiredArgsConstructorpublic class CompanySponsorshipController {    private final SearchVisaSponsorshipUseCase chatService;    @PostMapping("/sponsorship")    public Mono<CompanyVisaSponsorship> sponsorship(@Valid @RequestBody CompanyJobTitleDetailsRequest request) {        log.info("Income request for search company sponsorship:[{}]", request);        return chatService.getCompanyJobTitleResponse(request);    }}