package co.uk.offerland.job_track.infrastructure.controller;import co.uk.offerland.job_track.application.dto.user.InterviewFunnelResponse;import co.uk.offerland.job_track.application.usecases.funnel.InterviewFunnelCase;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.http.ResponseEntity;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.*;import reactor.core.publisher.Mono;import java.util.UUID;@Slf4j@RequiredArgsConstructor@RestController@Validated@RequestMapping("/api/users")public class InterviewFunnelController {    private final InterviewFunnelCase interviewFunnelCase;    @GetMapping("/{userId}/funnel")    public Mono<ResponseEntity<InterviewFunnelResponse>> registerUserId(@PathVariable UUID userId) {       log.info("Income interview funnel request userId: [{}]", userId);        return interviewFunnelCase.getFunnelStatistics(userId)                .map(response -> ResponseEntity.ok(response));    }}