package co.uk.offerland.job_track.infrastructure.controller;import org.springframework.http.ResponseEntity;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import co.uk.offerland.job_track.application.dto.user.UserCreateRequest;import co.uk.offerland.job_track.application.dto.user.UserCreateResponse;import co.uk.offerland.job_track.application.usecases.user.CreateUserUseCase;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;@Slf4j@RequiredArgsConstructor@RestController@Validated@RequestMapping("/api/users")public class UserController {    private final CreateUserUseCase userService;    @PostMapping("/register")    public ResponseEntity<UserCreateResponse> registerUserId(@RequestBody UserCreateRequest request) {       log.info("Income create user request: {}", request);        UserCreateResponse response = userService.createNewUser(request.getSource());        return ResponseEntity.ok(new UserCreateResponse(response.getUserId()));    }}