package co.uk.offerland.job_track.infrastructure.controller;import co.uk.offerland.job_track.application.dto.CompanyVisaSponsorshipDto;import co.uk.offerland.job_track.application.dto.RecognizeJobUrl;import co.uk.offerland.job_track.application.dto.RecognizeJobUrlResponse;import org.jsoup.Jsoup;import org.jsoup.nodes.Document;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import co.uk.offerland.job_track.application.dto.CompanyJobTitleDetailsRequest;import co.uk.offerland.job_track.application.usecases.job.VisaSponsorshipUseCase;import jakarta.validation.Valid;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import reactor.core.publisher.Mono;import java.io.IOException;@Slf4j@RestController@RequestMapping("/api/companies")@RequiredArgsConstructorpublic class VisaSponsorshipController {    private final VisaSponsorshipUseCase visaSponsorshipUseCase;    @PostMapping("/sponsorship")    public Mono<CompanyVisaSponsorshipDto> sponsorship(@Valid @RequestBody CompanyJobTitleDetailsRequest request) {        log.info("Income request for search company sponsorship:[{}]", request);        return visaSponsorshipUseCase.searchVisa(request);    }    @PostMapping("/recognize")    public Mono<RecognizeJobUrlResponse> recognize(@Valid @RequestBody RecognizeJobUrl request) {        log.info("Income request for search job:[{}]", request);        return visaSponsorshipUseCase.recognizeJob(request);    }}