package co.uk.offerland.job_track.infrastructure.converter;import co.uk.offerland.job_track.domain.entity.JobPhase;import jakarta.persistence.AttributeConverter;import jakarta.persistence.Converter;@Converter(autoApply = true)public class JobPhaseConverter implements AttributeConverter<JobPhase, String> {    @Override    public String convertToDatabaseColumn(JobPhase phase) {        return phase != null ? phase.getLabel() : null;    }    @Override    public JobPhase convertToEntityAttribute(String code) {        return JobPhase.fromCode(code);    }}