package co.uk.offerland.job_track.infrastructure.exception;import com.fasterxml.jackson.annotation.JsonInclude;import org.springframework.http.HttpStatus;import java.time.LocalDateTime;import java.util.Map;@JsonInclude(JsonInclude.Include.NON_NULL)public record ApiError(    LocalDateTime timestamp,    int status,    String error,    String message,    Map<String, String> details) {    public ApiError(HttpStatus status, String message) {        this(LocalDateTime.now(), status.value(), status.getReasonPhrase(), message, null);    }    public ApiError(HttpStatus status, String message, Map<String, String> details) {        this(LocalDateTime.now(), status.value(), status.getReasonPhrase(), message, details);    }}