package co.uk.offerland.job_track.infrastructure.exception;import lombok.Getter;import org.springframework.http.HttpStatus;@Getterpublic class PhaseNotFoundException extends RuntimeException {    private final HttpStatus httpStatus = HttpStatus.NOT_FOUND;    public PhaseNotFoundException(String phaseId) {        super(String.format("Phase not found with phaseId:[%s]", phaseId));    }}