package co.uk.offerland.job_track.infrastructure.util;import java.time.Instant;import java.time.LocalDateTime;import java.time.ZoneId;import java.time.ZoneOffset;import java.time.format.DateTimeFormatter;import lombok.experimental.UtilityClass;@UtilityClasspublic class DateTimeUtil {    public String convertInstantToString(Instant instant) {        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd-MM-YYYY")            .withZone(ZoneId.systemDefault());        return instant != null ? formatter.format(instant): "";    }    public Instant convertStringToInstant(String dateTimeStr) {        DateTimeFormatter formatter = DateTimeFormatter.ISO_LOCAL_DATE_TIME;        LocalDateTime localDateTime = LocalDateTime.parse(dateTimeStr, formatter);        return localDateTime.toInstant(ZoneOffset.UTC);    }}